import React, { useEffect, useState } from "react";
import "../Style/Banner.css";
import  axios from '../../../features/axios';
import Requests from '../../../features/Request'

const Banner = () => {
    const [movie, setMovie] = useState([]);


    useEffect(()=>{
        async function fetchData () {
            const request = await axios.get(Requests.fetchDocumentries);
            setMovie(
                request?.data.results[
                    Math.floor(Math.random() * request.data.results.length - 1)
                ]
            )
            return request
        }
        fetchData()
    }, [])
    console.log(movie)

  function truncate(string, n) {
    return string?.length > n ? string.substr(0, n - 1) + "..." : string;
  }

  return (
    <header
      className="banner"
      style={{
        backgroundImage: `url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAARMAAAC3CAMAAAAGjUrGAAAAA1BMVEUAAACnej3aAAAAR0lEQVR4nO3BAQ0AAADCoPdPbQ8HFAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAPBgxUwAAU+n3sIAAAAASUVORK5CYII=")`,
        backgroundSize: "cover",
        backgroundPosition: "center center",
      }}
    >
      <div className="banner-contents">
        <h1 className="banner-title">Movie Name</h1>
        <div className="banner-buttons">
          <button className="banner-button">Play</button>
          <button className="banner-button">My List</button>
        </div>
        <h3 className="banner-description">
          {truncate(
            ` this is a text descriptionthis is a text descriptionthis is a text descriptionthis is a text description
        this is a text descriptionthis is a text descriptionthis is a text description
        this is a text descriptionthis is a text descriptionthis is a text description
        this is a text descriptionthis is a text description`,
            150
          )}
        </h3>
      </div>

      <div className="banner-fadeButton" />
    </header>
  );
};

export default Banner;
