import React, { useEffect, useState } from "react";
import "../Style/Banner.css";
import axios from "../../../features/axios";
import Requests from "../../../features/Request";

const Banner = () => {
  const [movie, setMovie] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const request = await axios.get(Requests.fetchNetflixOrignals);
      setMovie(
        request.data.results[
          Math.floor(Math.random() * request.data.results.length - 1)
        ]
      );
      return request;
    }
    fetchData();
  }, []);
  console.log(movie);

  function truncate(string, n) {
    return string?.length > n ? string.substr(0, n - 1) + "..." : string;
  }

  return (
    <header
      className="banner"
      style={{
        backgroundImage: `${(movie.poster_path || movie.backdrop_path )&& `url("https://image.tmdb.org/t/p/original/${movie?.backdrop_path}")`}`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="banner-contents">
        <h1 className="banner-title">
          {movie?.title || movie?.name || movie?.original_name}
        </h1>
        <div className="banner-buttons">
          <button className="banner-button">Play</button>
          <button className="banner-button">My List</button>
        </div>
        <h3 className="banner-description">
          {truncate(`${movie?.overview}`, 150)}
        </h3>
      </div>

      <div className="banner-fadeButton" />
    </header>
  );
};

export default Banner;
